import { Form, Input, Button, Alert, Space } from 'antd';
import { useState } from 'react';
import { Link, redirect } from 'react-router-dom';

export default function RegisterView() {
    let [username, setUsername] = useState("");
    let [password, setPassword] = useState("");
    let [passwordRetype, setPasswordRetype] = useState("");
    let [phone, setPhone] = useState("");
    let [address, setAddress] = useState("");
    let [error, setError] = useState(false);
    let [errMsg, setErrMsg] = useState("");
    return ( 
        <div style={{width: "100%"}}>
            <Form style={{maxWidth: "300px", alignSelf: "center"}}>
                <Form.Item
                    name="username"
                    rules={[
                        {
                            required: true,
                            message: 'This field cannot be empty',
                        },
                    ]}
                >
                    <Input placeholder="Username" value={username} onChange={(e) => {
                        setUsername(e.target.value);
                    }} />
                </Form.Item>
                <Form.Item
                    name="password"
                        rules={[
                        {
                            required: true,
                            message: 'This field cannot be empty',
                        },
                    ]}
                >
                    <Input type="password" placeholder="Password" value={password} onChange={(e) => {
                        setPassword(e.target.value);
                    }} />
                </Form.Item>
                <Form.Item
                    name="passwordRetype"
                    rules={[
                        {
                            required: true,
                            message: 'This field cannot be empty',
                        },
                    ]}
                >
                    <Input placeholder="passwordRetype" value={passwordRetype} onChange={(e) => {
                        setPasswordRetype(e.target.value);
                    }} />
                </Form.Item>
                <Form.Item
                    name="phone"
                    rules={[
                        {
                            required: true,
                            message: 'This field cannot be empty',
                        },
                    ]}
                >
                    <Input placeholder="phone" value={phone} onChange={(e) => {
                        setPhone(e.target.value);
                    }} />
                </Form.Item>
                <Form.Item
                    name="address"
                    rules={[
                        {
                            required: true,
                            message: 'Please input your Username!',
                        },
                    ]}
                >
                    <Input placeholder="address" value={address} onChange={(e) => {
                        setAddress(e.target.value);
                    }} />
                </Form.Item>
                <Form.Item>
                    <Button onClick={(e) => {
                        e.preventDefault();
                        if (password != passwordRetype) {
                            setErrMsg("Password and password retype doesn't match");
                            setError(true);
                        }
                        fetch("http://localhost:4000/api/login", {
                            method: "post",
                            username: username,
                            password: password, 
                            phone: phone,
                            address: address
                        })
                        .then(res => {return res.json()})
                        .then(data => {
                            console.log(data);
                            if (data.msg === "OK") {
                                redirect(data.path);
                            } else {
                                setError(true);
                            }
                        })
                    }} style={{width: "100%"}}>
                    Register
                    </Button>
                </Form.Item>
            </Form>
            {error? 
            <Space direction='vertical'>
                <Alert message={errMsg} type="error" closable onClose={() => {
                    setError(false);
                }} />
            </Space> :
            <></>
            }
        </div>
    );
};
