import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';

function Logon( isLoggedIn ) {
    console.log(isLoggedIn);
    return (
        (isLoggedIn)? 
        (
            <div>
                <Link to={`/login`}>Login</Link>
            </div>
        ) : (
            <div>
                <Link to={`/logout`}>Logout</Link>
                <Link to={`/cart`}>Cart</Link>
            </div>
        )
    )
}

function NavBar() {
    return (
        <div>
            <Link to={`/`}>Home</Link>
            <Link to={`/gender/man`}>Man</Link>
            <Link to={`/gender/woman`}>Woman</Link>
            <Link to={`/gender/kid`}>Kid</Link>
        </div>
    );
}

function SearchBar() {
    return (
        <form>
            <input type="text" placeholder="Search shoe" />
            <input type="submit" />
        </form>
    );
}

let HomeView = function() {
    let [data, setData] = useState();


    useEffect(() => {
        let getCredentials = async () => {
            let fetchData = await fetch('http://localhost:4000/api/auth', {
                method: "post"
            });
            let rawData = fetchData.then(res => { return res.json() });
            let isLoggedIn = rawData.then(data => { return data });

            setData(isLoggedIn)
        }

        getCredentials();
    }, []);

    return (
        <div>
            <NavBar />
            <SearchBar />
            <Logon isLoggedIn={data} />
        </div>
    );
    
}

export default HomeView;
