import { Link } from 'react-router-dom';

let getCredentials = fetch('http://localhost:4000/api/auth', {
    method: "post",
    mode: "no-cors"
})
.then((state) => state)
.then();

function Logon( isLoggedIn ) {
    return (
        (isLoggedIn.isLoggedIn)? 
        (
            <div>
                <Link to={`/login`}>Login</Link>
            </div>
        ) : (
            <div>
                <Link to={`/logout`}>Logout</Link>
                <Link to={`/cart`}>Cart</Link>
            </div>
        )
    )
}

function NavBar() {
    return (
        <div>
            <Link to={`/`}>Home</Link>
            <Link to={`/gender/man`}>Man</Link>
            <Link to={`/gender/woman`}>Woman</Link>
            <Link to={`/gender/kid`}>Kid</Link>
        </div>
    );
}

function SearchBar() {
    return (
        <form>
            <input type="text" placeholder="Search shoe" />
            <input type="submit" />
        </form>
    );
}

let HomeView = function() {
    return (
        <div>
            <NavBar />
            <SearchBar />
            <Logon isLoggedIn={getCredentials} />
        </div>
    );
}

export default HomeView;
