import { Menu, Input } from 'antd';
import { Link, useNavigate } from 'react-router-dom';
import { useState } from 'react';

function Logon( {props} ) {
    let navigate = useNavigate();
    let [isLoggedIn, setLoggedIn] = useState(props.isLoggedIn)
    if (isLoggedIn) {
        return (
            <>
                <Menu.Item key={"logout"}>
                    <Link onClick={(e) => {
                        e.preventDefault();
                        fetch('http://localhost:4000/api/logout', {
                            credentials: 'include',
                            method: 'post'
                        })
                        .then(() => {
                            
                            navigate('/');
                        })
                    }}>Log out</Link>
                </Menu.Item>
                <Menu.Item key={"cart"}>
                    <Link to={`/cart`}>Cart</Link>
                </Menu.Item>
            </>
        );
    } else {
        return (
            <Menu.Item key={"login"}>
                <Link to={`/login`}>Log in</Link>
            </Menu.Item>
        )
    }
}

export default function NavBar({ props }) {

    return (
        <>
            <Menu mode="horizontal" style={{width: "100%"}}>
                <Menu.Item key={"home"}>
                    <Link to={`/`}>Home</Link>
                </Menu.Item>
                <Menu.Item key={"man"}>
                    <Link to={`/gender/man`}>Man</Link>
                </Menu.Item>
                <Menu.Item key={"woman"}>
                    <Link to={`/gender/woman`}>Woman</Link>
                </Menu.Item>
                <Menu.Item key={"kid"}>
                    <Link to={`/gender/kid`}>Kid</Link>
                </Menu.Item>
                <Menu.Item key={"search"} style={{ alignSelf: 'right' }}>
                    <Input.Search
                        placeholder="input search text"
                        style={{ width: 200 }}
                    />
                </Menu.Item>
                <Logon props={props} />
            </Menu>
            <br/>
        </>
    );
}
