import { Menu, Button, Drawer } from 'antd';
import { Link, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { SearchOutlined } from '@ant-design/icons';

function Logon( {props} ) {
    let navigate = useNavigate();
    let [isLoggedIn, setLoggedIn] = useState(props.isLoggedIn)
    if (isLoggedIn) {
        return (
            <>
                <Menu.Item key={"logout"}>
                    <Link onClick={(e) => {
                        e.preventDefault();
                        fetch('http://localhost:4000/api/logout', {
                            credentials: 'include',
                            method: 'post'
                        })
                        .then(() => {
                            setLoggedIn(false);
                            navigate('/');
                        })
                    }}>Log out</Link>
                </Menu.Item>
                <Menu.Item key={"cart"}>
                    <Link to={`/cart`}>Cart</Link>
                </Menu.Item>
            </>
        );
    } else {
        return (
            <Menu.Item key={"login"}>
                <Link to={`/login`}>Log in</Link>
            </Menu.Item>
        )
    }
}

export default function NavBar({ props }) {

    const [open, setOpen] = useState(false);

    return (
        <>
            <Menu mode="horizontal" selectedKeys={[]} style={{width: "100%"}}>
                <Menu.Item key={"home"}>
                    <Link to={`/`}>Home</Link>
                </Menu.Item>
                <Menu.Item key={"man"}>
                    <Link to={`/gender/man`}>Man</Link>
                </Menu.Item>
                <Menu.Item key={"woman"}>
                    <Link to={`/gender/woman`}>Woman</Link>
                </Menu.Item>
                <Menu.Item key={"kid"}>
                    <Link to={`/gender/kid`}>Kid</Link>
                </Menu.Item>
                <Menu.Item key={"search"} style={{ alignSelf: 'right' }}>
                    <Button type="primary" shape="circle" icon={<SearchOutlined />} onClick={() => {

                    }} />
                </Menu.Item>
                <Logon props={props} />
            </Menu>
            <br/>
        </>
    );
}
