import { Button, Form, Input, Select, Spin, Menu } from 'antd';
import { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
const { Search } = Input;

function Logon( props ) {
    if (props) {
        return (
            <div>
                <Link to={`/logout`}>Logout</Link>
                <Link to={`/cart`}>Cart</Link>
            </div>
        );
    } else {
        return (
            <div>
                <Link to={`/login`}>Login</Link>
            </div>
        )
    }
}

function NavBar({ props }) {

    return (
        <Menu mode="horizontal" >
            <Menu.Item key={"home"}>
                <Link to={`/`}>Home</Link>
            </Menu.Item>
            <Menu.Item key={"man"}>
                <Link to={`/gender/man`}>Man</Link>
            </Menu.Item>
            <Menu.Item key={"woman"}>
                <Link to={`/gender/woman`}>Woman</Link>
            </Menu.Item>
            <Menu.Item key={"kid"}>
                <Link to={`/gender/kid`}>Kid</Link>
            </Menu.Item>
            <Menu.Item key={"search"} style={{ float:"right" }}>
                <Search
                    placeholder="input search text"
                    style={{ width: 200 }}
                />
            </Menu.Item>
            (props) ? 
            <Menu.Item key={"logout"}>
                <Link to={`/logout`}>Lo</Link>
            </Menu.Item>
        </Menu>
    );
}

function SearchBar() {
    return (
        <Form style={{width: 200, float: "right"}}>
            <Input type="text" placeholder="Search shoe" />
            <Input type="submit" value="Search" />
        </Form>
    );
}



function ShoeImage({ imageURL }) {
    return (
        <div>
            <img alt='shoeImage' src={imageURL} width="500px" height="500px" />
        </div>
    )
}

function OrderForm({ size }) {

    let sizes = [];

    size.forEach(element => {
        sizes.push(<option key={element} value={element}>{element}</option>);
    });


    return (
        <div>
            <Select style={{width: 75}} placeholder="Size" onChange={(e) => {}}>
                {sizes}
            </Select><br/>
            <Button class="addToCart">Add to cart</Button>
        </div>
    )
}

function Info({ name, gender, price }) {
    return (
        <div>
            <h2>{name}</h2>
            <h3>{gender}</h3>
            <h4>{price}</h4>
        </div>
    )
}

function Shoe({ name, gender, price, size, imageURL }) {
    return (
        <div>
            <div style={{width: "50%", float: "right"}}>
                <Info 
                    name={name} 
                    gender={gender}
                    price={price}
                />
                <OrderForm size={size} />
            </div>
            <div style={{width: "50%", float: "left"}}>
                <ShoeImage imageURL={imageURL} />
            </div>
        </div>

    )
}

export default function ShoeView() {
    let [credential, setCredential] = useState();
    let [shoeDetail, setShoeDetail] = useState();
    let [sortBy, setSortBy] = useState();

    let param = useParams();
    
    useEffect(() => {
        fetch('http://localhost:4000/api/auth', {
            method: "post"
        })
        .then(res => { return res.json() })
        .then(data => { setCredential(data.isLoggedIn) });

        fetch(`http://localhost:4000/api/shoe/${param.sid}`)
        .then(res => { return res.json() })
        .then(data => { 
            setShoeDetail(data); 
        });
    }, [param]);

    if (shoeDetail && credential) {
        return (
            <div>
                <NavBar props={credential} />
                <Shoe 
                    name={shoeDetail.shoe.name}
                    gender={shoeDetail.shoe.gender}
                    price={shoeDetail.shoe.price}
                    size={shoeDetail.size}
                    imageURL={shoeDetail.shoe.imageURL}
                />
            </div>
        )
    } else {
        return (
            <div>
                <Spin />
            </div>
        )
    }
}
