import { Link, useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { Card } from 'react-bootstrap';

function Logon( props ) {
    if (props) {
        return (
            <div>
                <Link to={`/logout`}>Logout</Link>
                <Link to={`/cart`}>Cart</Link>
            </div>
        );
    } else {
        return (
            <div>
                <Link to={`/login`}>Login</Link>
            </div>
        )
    }
}

function NavBar() {
    return (
        <div>
            <Link to={`/`}>Home</Link>
            <Link to={`/gender/man`}>Man</Link>
            <Link to={`/gender/woman`}>Woman</Link>
            <Link to={`/gender/kid`}>Kid</Link>
        </div>
    );
}

function SearchBar() {
    return (
        <form>
            <input type="text" placeholder="Search shoe" />
            <input type="submit" />
        </form>
    );
}

function Sort() {
    return (
        <div>
            <select>
                <option value="priceAsc">Sort by price ascending</option>
                <option value="priceDesc">Sort by price descending</option>
                <option value="nameAsc">Sort by name ascending</option>
                <option value="nameDesc">Sort by name descending</option>
            </select>
        </div>
    )
}

function ShoeCard({ props }) {

    console.log(props.sid);

    return (
        <div>
            <Card
                className='card-shoe'
                hoverable
                style={{
                    width: 240,
                    margin: "20px",
                    borderRadius: "20px",
                    overflow: "hidden",
                }}
                cover={
                    <Link to={{pathname: `/shoe/${props.sid}`}}>
                        <img alt="example" src={props.imageURL} height='200' width='200' />
                    </Link>
                }
            >
                <meta title={props.name} description={props.gender} />
                <br />
            </Card>
        </div>
        /* <div>
            <Link to="/shoe/{sid}">
                <img src={props.imageURL} width="100px" height="100px" />
                <h2>{props.name}</h2>
            </Link>
            <h3>{props.gender}</h3>
            <h4>{props.price}</h4>
        </div> */
    )
}

function ShoeRack({ props }) {

    let cards = [];

    if (props !== undefined) {
        props.shoes.forEach(element => {
            cards.push(
                <ShoeCard props={element} />
            );
        });
    }

    return (
        <div>
            {cards}
        </div>
    );
}

export default function GoodsView() {
    let [credential, setCredential] = useState();
    let [shoes, setShoes] = useState();

    let param = useParams();
    
    useEffect(() => {

        fetch('http://localhost:4000/api/auth', {
            method: "post"
        })
        .then(res => { return res.json() })
        .then(data => { setCredential(data.isLoggedIn) });

        fetch(`http://localhost:4000/api/gender/${param.gender}`)
        .then(res => { return res.json() })
        .then(data => { 
            setShoes(data); 
            console.log(data);
        });
    }, []);



    return (
        <div>
            <div>
                <NavBar />
                <SearchBar />
                <Logon props={credential} />
            </div>
            <div style={{ width: "80%" , float: "right" }}>
                <ShoeRack props={shoes}/>
            </div>
            <div style={{ width: "20%" , float: "left" }}>
                <Sort />
            </div>
        </div>
    )
}
