import { Link, useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { Pagination, Card, Select, Spin } from 'antd';
import NavBar from './navBar';

function Sort() {
    return (
        <div style={{ width: "20%" , float: "left" }}>
            <Select style={{width: 200}} placeholder="Sort" onChange={(e) => {}}>
                <Select.Option value="price">Sort by price ascending</Select.Option>
                <Select.Option value="name">Sort by name ascending</Select.Option>
            </Select>
        </div>
    )
}

function ShoeCard({ sid, name, imageURL, gender, price }) {

    return (
        <div>
            <Card
                className='shoe-card'
                hoverable
                style={{
                    width: 300,
                    margin: "20px",
                    borderRadius: "20px",
                    overflow: "hidden",
                }}
                cover={
                    <Link to={{pathname: `/shoe/${sid}`}}>
                        <img alt="example" src={imageURL} height='300' width='300' />
                    </Link>
                }
            >
                <Card.Meta title={name} description={gender}  />
                <p>{price}</p>
                <br />
            </Card>
        </div>
    )
}

function ShoeRack({ props }) {

    let shoeCards = [];

    props.shoes.forEach(element => {
        shoeCards.push(
            <ShoeCard 
                sid={element.sid} 
                name={element.name} 
                gender={element.gender} 
                price={element.price} 
                imageURL={element.imageURL}
            />
        );
    });

    return (
        <div className='shoe-rack' style={{ width: "80%" , float: "right" }}>
            <div  style={{
                display: "flex",
                flexDirection: "row",
                flexDrap: "wrap"
            }}>
                {shoeCards}
            </div>
            <div>
                <Paging />
            </div>
        </div>
    );
}

function Paging() {
    return (
        <div>
            <Pagination
                defaultCurrent={1}
                defaultPageSize={6}
            />
        </div>
    );
}

export default function GoodsView() {
    let [credential, setCredential] = useState();
    let [allShoes, setAllShoes] = useState();
    let [sort, setSort] = useState()

    let param = useParams();
    
    useEffect(() => {

        fetch('http://localhost:4000/api/auth', {
            method: "post"
        })
        .then(res => { return res.json() })
        .then(data => { setCredential(data) });

        fetch(`http://localhost:4000/api/gender/${param.gender}`)
        .then(res => { return res.json() })
        .then(data => { 
            setAllShoes(data); 
        });
    }, [param]);

    if (allShoes) {
        return (
            <div>
                <NavBar credential={credential} />
                <ShoeRack props={allShoes} />
                <Sort />
            </div>
        )
    } else {
        return (
            <div>
                <NavBar credential={credential} />
                <Spin />
            </div>
        )
    }
}
