import { Link, useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { Pagination, Card, Select, Form, Input, Button } from 'antd';
const { Meta } = Card;

function Logon( props ) {
    if (props) {
        return (
            <div>
                <Link to={`/logout`}>Logout</Link>
                <Link to={`/cart`}>Cart</Link>
            </div>
        );
    } else {
        return (
            <div>
                <Link to={`/login`}>Login</Link>
            </div>
        )
    }
}

function NavBar() {
    return (
        <div>
            <Link to={`/`}>
                <Button value="Home" style={{}} />
            </Link>
            <Link to={`/gender/man`}>Man</Link>
            <Link to={`/gender/woman`}>Woman</Link>
            <Link to={`/gender/kid`}>Kid</Link>
        </div>
    );
}

function SearchBar() {
    return (
        <Form style={{width: 200}}>
            <Input type="text" placeholder="Search shoe" />
            <Input type="submit" value="Search" />
        </Form>
    );
}

function Sort() {
    return (
        <div>
            <Select style={{width: 200}} onChange={(e) => {}}>
                <option value="price">Sort by price ascending</option>
                <option value="name">Sort by name ascending</option>
            </Select>
        </div>
    )
}

function ShoeCard({ sid, name, imageURL, gender, price }) {

    return (
        <div>
            <Card
                className='shoe-card'
                hoverable
                style={{
                    width: 200,
                    margin: "20px",
                    borderRadius: "20px",
                    overflow: "hidden",
                }}
                cover={
                    <Link to={{pathname: `/shoe/${sid}`}}>
                        <img alt="example" src={imageURL} height='200' width='200' />
                    </Link>
                }
            >
                <Meta title={name} description={gender} />
                <br />
            </Card>
        </div>
    )
}

function ShoeRack({ props }) {

    let shoeCards = [];

    if (props !== undefined) {
        props.shoes.forEach(element => {
            shoeCards.push(
                <ShoeCard 
                    sid={element.sid} 
                    name={element.name} 
                    gender={element.gender} 
                    price={element.price} 
                    imageURL={element.imageURL}
                />
            );
        });
    }

    return (
        <div className='shoe-rack' style={{
            display: "flex",
            flexDirection: "row",
            flexDrap: "wrap"
        }}>
            {shoeCards}
        </div>

    );
}

function Paging() {
    return (
        <Pagination
            defaultCurrent={1}
            defaultPageSize={10}
        />
    );
}

export default function GoodsView() {
    let [credential, setCredential] = useState();
    let [allShoes, setAllShoes] = useState();
    let [sortBy, setSortBy] = useState()

    let param = useParams();
    
    useEffect(() => {

        fetch('http://localhost:4000/api/auth', {
            method: "post"
        })
        .then(res => { return res.json() })
        .then(data => { setCredential(data.isLoggedIn) });

        fetch(`http://localhost:4000/api/gender/${param.gender}`)
        .then(res => { return res.json() })
        .then(data => { 
            setAllShoes(data); 
            console.log(data);
        });
    }, [param]);



    return (
        <div>
            <div>
                <NavBar />
                <SearchBar />
                <Logon props={credential} />
            </div>
            <div style={{ width: "80%" , float: "right" }}>
                <ShoeRack props={allShoes} />
                <Paging />
            </div>
            <div style={{ width: "20%" , float: "left" }}>
                <Sort />
            </div>
        </div>
    )
}
